import {querySelectorAll, NodeListOf} from 'dom-helpers';

export function removeGtagScripts(): void {
  const gtagScripts: NodeListOf<HTMLScriptElement> = querySelectorAll('script[src^="https://www.googletagmanager.com"]');
  gtagScripts.forEach((script) => script.parentNode.removeChild(script));
}
