import { FirebaseApp, FirebaseAppCheck, FirebaseNamespace } from '@firebase/app-types';

/**
 * Check if the `app` parameter is provided
 * @param app - The Firebase app instance
 * @returns `true` if `app` is provided, `false` otherwise
 */
function isAppProvided(app?: FirebaseApp): app is FirebaseApp {
  return !!app;
}

/**
 * Register the App Check extension
 */
export function registerAppCheck(): void {
  if (isAppProvided()) {
    // Do something with the provided app instance
  } else {
    // Do something without an app instance
  }
}

/**
 * Define extension behavior of `registerAppCheck`
 */
declare module '@firebase/app-compat' {
  interface FirebaseNamespace {
    appCheck(app?: FirebaseApp): FirebaseAppCheck;
  }
  interface FirebaseApp {
    appCheck(): FirebaseAppCheck;
  }
}

export default registerAppCheck;
