import { FirebaseApp } from '@firebase/app';
import { Provider } from '@firebase/component';
import { AppCheckTokenInternal } from './types';

interface AppCheckRequest {
  url: string;
  body: {
    [key: string]: string;
  };
}

/**
 * Exchanges a token with the App Check server.
 *
 * @param request - The request to exchange the token.
 * @param heartbeatServiceProvider - The heartbeat service provider.
 * @returns A promise that resolves to the App Check token.
 */
export declare function exchangeToken(
  request: AppCheckRequest,
  heartbeatServiceProvider: Provider<'heartbeat'>
): Promise<AppCheckTokenInternal>;

/**
 * Gets the request to exchange a reCAPTCHA v3 token.
 *
 * @param app - The Firebase app.
 * @param reCAPTCHAToken - The reCAPTCHA v3 token.
 * @returns The request to exchange the token.
 */
export declare function getExchangeRecaptchaV3TokenRequest(
  app: FirebaseApp,
  reCAPTCHAToken: string
): AppCheckRequest;

