import * as exp from '@firebase/auth/internal';

export const Persistence = {
  LOCAL: 'local',
  NONE: 'none',
  SESSION: 'session',
};

export function validatePersistenceArgument(auth: exp.Auth, persistence: string): void {
  if (!Object.values(Persistence).includes(persistence)) {
    throw new Error(`Invalid persistence value: ${persistence}`);
  }
}

