// Import the Firebase Authentication module
import firebase from 'firebase/app';
import 'firebase/auth-compat';

// Initialize Firebase and configure authentication
const config = {
  apiKey: "your-api-key",
  authDomain: "your-auth-domain",
  // Add the rest of your Firebase configuration here
};
firebase.initializeApp(config);

// Use the compatibility layer to enable authentication features
const auth = firebase.auth();

// Example usage: sign in a user with email and password
function signIn(email, password) {
  auth.signInWithEmailAndPassword(email, password)
    .then((userCredential) => {
      // Signed in
      const user = userCredential.user;
      console.log(`User ${user.email} signed in.`);
    })
    .catch((error) => {
      const errorCode = error.code;
      const errorMessage = error.message;
      console.error(`Error signing in: ${errorCode} - ${errorMessage}`);
    });
}

