import { Auth, UserCredential } from '../../model/public_types';

/**
 * Asynchronously signs in using a custom token.
 *
 * @param auth - The Auth instance.
 * @param customToken - The custom token to sign in with.
 *
 * @returns A promise that resolves with the user credential if the sign-in is successful,
 * or rejects with an error if the sign-in fails.
 *
 * @throws Error with a message indicating the reason of the sign-in failure.
 */
export async function signInWithCustomToken(auth: Auth, customToken: string): Promise<UserCredential> {
  try {
    const result = await auth.signInWithCustomToken(customToken);
    return result;
  } catch (error) {
    throw new Error(`Failed to sign in with custom token: ${error.message}`);
  }
}
