/**
 * Enums for Browser name.
 */
export declare const BrowserName: {
    ANDROID: "Android";
    BLACKBERRY: "Blackberry";
    EDGE: "Edge";
    FIREFOX: "Firefox";
    IE: "IE";
    IEMobile: "IEMobile";
    OPERA: "Opera";
    OTHER: "Other";
    CHROME: "Chrome";
    SAFARI: "Safari";
    SILK: "Silk";
    WEBOS: "Webos";
};

/**
 * Determine the browser for the purposes of reporting usage to the API
 * @param userAgent - The user agent string to analyze
 * @returns The browser name, or an unknown browser name if the user agent is not recognized
 */
export declare function getBrowserName(userAgent: string): BrowserName | "Other";

/**
 * Check if a user agent string corresponds to Firefox
 * @param ua - The user agent string to analyze (optional)
 * @returns True if the user agent corresponds to Firefox, false otherwise
 */
export declare function isFirefox(ua?: string): ua is "Firefox" | "Gecko";

/**
 * Check if a user agent string corresponds to Safari
 * @param userAgent - The user agent string to analyze (optional)
 * @returns True if the user agent corresponds to Safari, false otherwise
 */
export declare function isSafari(userAgent?: string): ua is "Safari" | "Mobile/2.0 Safari";

/**
 * Check if a user agent string corresponds to Chrome for iOS
 * @param userAgent - The user agent string to analyze (optional)
 * @returns True if the user agent corresponds to Chrome for iOS, false otherwise
 */
export declare function isChromeIOS(userAgent?: string): ua is "CriOS";

