import { ConfigInternal } from '../../model/auth';

/**
 * Returns the URL for the authentication emulator, with an optional path.
 * @param config The authentication configuration object.
 * @param path An optional path to append to the URL.
 * @returns The URL for the authentication emulator.
 */
export function getAuthEmulatorUrl(config: ConfigInternal, path?: string): string {
  return _emulatorUrl(config, path);
}

