import { LogLevel } from '@firebase/logger';

export { LogLevel };

// Re-export the logger functions from @firebase/logger
export {
  logDebug as _logDebug,
  logWarn as _logWarn,
  logError as _logError,
} from '@firebase/logger';

// Export a function to set the log level
export function setLogLevel(newLevel: LogLevel): void {
  _setLogLevel(newLevel);
}

// Export a function to get the current log level
export function getLogLevel(): LogLevel {
  return _getLogLevel();
}
