import { FirebaseApp } from '@firebase/app';
import { Auth, Dependencies as AuthDependencies } from '../../model/public_types';
import { AuthImpl } from './auth_impl';

/**
 * Enum for persistence options.
 */
export enum Persistence {
  LOCAL = 'local',
  SESSION = 'session',
  NONE = 'none',
}

/**
 * Initializes an {@link Auth} instance with fine-grained control over
 * {@link AuthDependencies}.
 *
 * @public
 * @param app - The FirebaseApp instance.
 * @param deps - The AuthDependencies to configure the Auth instance.
 * @returns An initialized Auth instance.
 */
export function initializeAuth(app: FirebaseApp, deps?: Partial<AuthDependencies>): Auth {
  const auth = new AuthImpl(app);

  // Set default values for missing dependencies
  const dependencies: AuthDependencies = {
    persistence: Persistence.LOCAL,
    popupRedirectResolver: undefined,
    ...deps,
  };

  _initializeAuthInstance(auth, dependencies);

  return auth;
}

/**
 * Initializes the Auth instance with the given dependencies.
 * @internal
 * @param auth - The Auth instance to initialize.
 * @param deps - The AuthDependencies to configure the Auth instance.
 */
export function _initializeAuthInstance(auth: AuthImpl, deps: AuthDependencies): void {
  // Implementation for initializing the Auth instance with the given dependencies
}
