import { PhoneOrOauthTokenResponse } from '../../api/authentication/mfa';
import { SignInWithPhoneNumberRequest } from '../../api/authentication/sms';
import { AuthInternal } from '../../model/auth';
import { IdTokenResponse } from '../../model/id_token';
import { AuthCredential } from './auth_credential';

export interface PhoneAuthCredentialParameters {
  verificationId?: string;
  verificationCode?: string;
  phoneNumber?: string;
  temporaryProof?: string;
}

/**
 * Represents the credentials returned by {@link PhoneAuthProvider}.
 *
 * @public
 */
export class PhoneAuthCredential extends AuthCredential {
  private readonly params: PhoneAuthCredentialParameters;

  constructor(params: PhoneAuthCredentialParameters = {}) {
    super();
    this.params = params;
  }

  static fromVerification(verificationId: string, verificationCode: string): PhoneAuthCredential {
    return new PhoneAuthCredential({verificationId, verificationCode});
  }

  static fromTokenResponse(phoneNumber: string, temporaryProof: string): PhoneAuthCredential {
    return new PhoneAuthCredential({phoneNumber, temporaryProof});
  }

  _getIdTokenResponse(auth: AuthInternal): Promise<PhoneOrOauthTokenResponse> {
    // Implementation goes here
  }

  _linkToIdToken(auth: AuthInternal, idToken: string): Promise<IdTokenResponse> {
    // Implementation goes here
  }

  _getReauthenticationResolver(auth: AuthInternal): Promise<IdTokenResponse> {
    // Implementation goes here
  }

  _makeVerificationRequest(): SignInWithPhoneNumberRequest {
    // Implementation goes here
  }

  toJSON(): object {
    return {...this.params};
  }

  static fromJSON(json: object | string): PhoneAuthCredential | null {
    if (typeof json === 'string') {
      try {
        json = JSON.parse(json);
      } catch (e) {
        return null;
      }
    }

    if (json && typeof json === 'object') {
      return new PhoneAuthCredential(json);
    }

    return null;
  }
}
