import { UserCredential, Auth as AuthInternal, User as UserInternal } from '../../model/public_types' assert { type: 'json' };
import { AuthCredential } from '../credentials';

/**
 * Asynchronously signs in with the given credentials.
 *
 * @param auth - The {@link Auth} instance.
 * @param credential - The auth credential.
 * @returns The signed-in user's credentials.
 * @throws {@link AuthError}
 * @template TUserCredential = UserCredential
 */
export declare function _signInWithCredential(auth: AuthInternal, credential: AuthCredential, bypassAuthState?: boolean): Promise<TUserCredential>;

/**
 * Links the user account with the given credentials.
 *
 * @param user - The user.
 * @param credential - The auth credential.
 * @returns The linked user's credentials.
 * @throws {@link AuthError}
 * @template TUserCredential = UserCredential
 */
export declare function linkWithCredential(user: UserInternal, credential: AuthCredential): Promise<TUserCredential>;

/**
 * Re-authenticates a user using a fresh credential.
 *
 * @param user - The user.
 * @param credential - The auth credential.
 * @returns The re-authenticated user's credentials.
 * @throws {@link AuthError}
 * @template TUserCredential = UserCredential
 */
export declare function reauthenticateWithCredential(user: UserInternal, credential: AuthCredential): Promise<TUserCredential>;

/**
 * Asynchronously signs in with the given credentials.
 *
 * @param auth - The {@link Auth} instance.
 * @param credential - The auth credential.
 * @returns The signed-in user's credentials.
 * @throws {@link AuthError}
 * @template TUserCredential = UserCredential
 */
export declare function signInWithCredential(auth: Auth, credential: AuthCredential): Promise<TUserCredential>;
