import { ProviderAssociatedObject, Provider } from './types'; // importing the types from a separate file

/**
 * Converts a set of UserInfo provider data to a set of names
 * @param providerData - the provider data to convert
 * @returns a Set of provider names
 */
export function providerDataAsNames<T extends ProviderAssociatedObject>(providerData: T[]): Set<Provider> {
  return new Set(providerData.map(provider => provider.providerId!)); // using non-null assertion operator to assert that providerId is not null or undefined
}
