export type MultiFactorErrorData = MultiFactorErrorPublic['customData'] & {
    _serverResponse: IdTokenMfaResponse;
};

export class MultiFactorError extends FirebaseError implements MultiFactorErrorPublic {
    readonly operationType: OperationType;
    readonly user?: UserInternal | undefined;
    readonly customData: MultiFactorErrorData;

    constructor(message: string, customData: MultiFactorErrorData, operationType: OperationType, user?: UserInternal) {
        super(message);
        this.operationType = operationType;
        this.user = user;
        this.customData = customData;
    }

    static fromErrorAndOperation(auth: AuthInternal, error: FirebaseError, operationType: OperationType, user?: UserInternal): MultiFactorError {
        const customData = {
            ...error.customData,
            _serverResponse: error._serverResponse as IdTokenMfaResponse,
        };
        return new MultiFactorError(error.message, customData, operationType, user);
    }
}

