import AsyncStorage from '@react-native-community/async-storage';

/**
 * A type representing a persistence object that wraps `AsyncStorage` imported from
 * `react-native` or `@react-native-community/async-storage`, and can
 * be used in the persistence dependency field in {@link initializeAuth}.
 */
export type Persistence = {
  setItem: (key: string, value: string) => Promise<void>;
  getItem: (key: string) => Promise<string | null>;
  removeItem: (key: string) => Promise<void>;
};

/**
 * Returns a persistence object that wraps `AsyncStorage` imported from
 * `@react-native-community/async-storage`, and can
 * be used in the persistence dependency field in {@link initializeAuth}.
 *
 * @public
 */
export function getReactNativePersistence(): Persistence {
  return {
    setItem: async (key, value) => {
      await AsyncStorage.setItem(key, value);
    },
    getItem: async key => {
      return AsyncStorage.getItem(key);
    },
    removeItem: async key => {
      await AsyncStorage.removeItem(key);
    },
  };
}
